const __pageData = JSON.parse('{"title":"Why use a service bus?","description":"","frontmatter":{},"headers":[{"level":2,"title":"Core","slug":"core","link":"#core","children":[]},{"level":2,"title":"Messages","slug":"messages","link":"#messages","children":[]},{"level":2,"title":"Queues / Streams","slug":"queues-streams","link":"#queues-streams","children":[]},{"level":2,"title":"Service bus","slug":"service-bus","link":"#service-bus","children":[]},{"level":2,"title":"Message Types","slug":"message-types","link":"#message-types","children":[{"level":3,"title":"Command message","slug":"command-message","link":"#command-message","children":[]},{"level":3,"title":"Starting a process","slug":"starting-a-process","link":"#starting-a-process","children":[]},{"level":3,"title":"Lower-level functions (RPC)","slug":"lower-level-functions-rpc","link":"#lower-level-functions-rpc","children":[]},{"level":3,"title":"Event message","slug":"event-message","link":"#event-message","children":[]},{"level":3,"title":"Document message","slug":"document-message","link":"#document-message","children":[]}]},{"level":2,"title":"Coupling","slug":"coupling","link":"#coupling","children":[{"level":3,"title":"Behavioural coupling","slug":"behavioural-coupling","link":"#behavioural-coupling","children":[]},{"level":3,"title":"Temporal coupling","slug":"temporal-coupling","link":"#temporal-coupling","children":[]}]}],"relativePath":"concepts/why.md"}');
